    public static String transferAngleAsFlatNotchLocation(String binList, long FlatNotchLocation, String rowCount, String columnCount) {
        String binListb = "";
        char[] bins = binList.toCharArray();
        bins = transferAngle(bins, String.valueOf(FlatNotchLocation%360), Integer.valueOf(rowCount), Integer.valueOf(columnCount));
        binListb = String.valueOf(bins);
        return binListb;
    }
    
    public static String transferAngleAsFlatNotchLocation(String binList, long FlatNotchLocation, int rowCount, int columnCount) {
        String binListb = "";
        char[] bins = binList.toCharArray();
        bins = transferAngle(bins, String.valueOf(FlatNotchLocation % 360), rowCount, columnCount);
        binListb = String.valueOf(bins);
        return binListb;
    }
    
    private static char[] transferAngle(char[] src, String angle, int row, int col) {
        logger.info("对mapping进行角度旋转，旋转角度为：" + angle);
        if ("0".equals(angle)) {
            return src;
        }
        char[] tmp = transferArgs1(src, row, col);
        if ("90".equals(angle)) {
            return tmp;
        }
        if ("180".equals(angle)) {
            tmp = transferArgs1(tmp, col, row);
        }
        if ("270".equals(angle)) {
            tmp = transferArgs1(tmp, col, row);
            tmp = transferArgs1(tmp, row, col);
        }
        return tmp;
    }
    
