    public static char[][] toDoubleArray(String bins, int row, int col){
        char[] src = bins.toCharArray();
        char[][] doubleArray = new char[row][col];
        int count = 0;
        for (int i = 0; i < row; i++) {
            for (int j = 0; j < col; j++) {
                doubleArray[i][j] = src[count];
                count++;
            }
        }
        return doubleArray;
    }
    
    public static Map<String, Integer> blankCheck(char blankBin,String bins, int row, int col) {
        logger.info("blankCheck start ");
        Map<String, Integer> map = new HashMap<String, Integer>();
        char[][] binArray = WaferTransferUtil.toDoubleArray(bins, row, col);
        int up = 0;
        boolean upEnd = false;
        for (int i = 0; i < row; i++) {
            if(upEnd){
                break;
            }
            for (int j = 0; j < col; j++) {
                if (blankBin == binArray[i][j]) {
                    continue;
                } else {
                    up = i;
                    map.put("UP", i);
                    upEnd = true;
                    break;
                }
            }
        }
        
        boolean downEnd = false;
        for (int i = row - 1; i >0; i--) {
            if (downEnd) {
                break;
            }
            for (int j = 0; j < col; j++) {
                if (blankBin == binArray[i][j]) {
                    continue;
                } else {
                    map.put("DOWN", i);
                    downEnd = true;
                    break;
                }
            }
        }
        
        boolean leftEnd = false;
        for (int i = 0; i < col; i++) {
            if (leftEnd) {
                break;
            }
            for (int j = 0; j < row; j++) {
                if (blankBin == binArray[j][i]) {
                    continue;
                } else {
                    up = i;
                    map.put("LEFT", i);
                    leftEnd = true;
                    break;
                }
            }
        }
        
        boolean rightEnd = false;
        for (int i = col - 1; i > 0; i--) {
            if (rightEnd) {
                break;
            }
            for (int j = 0; j < row; j++) {
                if (blankBin == binArray[j][i]) {
                    continue;
                } else {
                    map.put("RIGHT", i);
                    rightEnd = true;
                    break;
                }
            }
        }
        logger.info("blankCheck end ");
        return map;
    }
    
    private static char[] transferArgs1(char[] src, int row, int col) {
        int i, j;
        char[][] num = new char[row][col];
        int count = 0;
        for (i = 0; i < row; i++) {
            for (j = 0; j < col; j++) {
                if (i == 0) {
                    num[i][j] = src[j];
                } else {
                    num[i][j] = src[count];
                }
                count++;
            }
        }
        char[][] num2 = new char[col][row];
        i = 0;
        for (int k = num.length - 1; k > -1; k--) {
            int tmp = 0;
            for (int p = 0; p < num2.length; p++) {
                num2[tmp][k] = num[i][p];
                tmp++;
            }
            i++;
        }
        char[] result = new char[src.length];
        List results = new LinkedList();
        for (i = 0; i < num2.length; i++) {
            for (j = 0; j < num2[i].length; j++) {
                results.add(num2[i][j]);
//                 System.out.println("num2" + "[" + i + "]" + "[" + j + "]" + "---" + num2[i][j]);
            }
        }
        for (i = 0; i < results.size(); i++) {
            result[i] = (char) results.get(i);
//            System.out.print(result[i] + " ");
        }
        return result;
    }
