mapper.select:
select 

from ar_device_realtime_para
where del_flag='0'

    and DEVICE_CODE = #{deviceCode}


    and UPDATE_CNT = #{updateCnt}


    and REMARKS = #{remarks}

order by update_date desc

select 

from ar_device_realtime_para
where del_flag='0'

    and DEVICE_CODE = #{deviceCode}


    and  PARA_NAME = #{paraName}


    and  RECIPE_ROW_ID = #{recipeRowId}


    and  REMARKS = #{remarks}

order by create_date desc

select max(update_cnt) from ar_device_realtime_para where 1=1
and DEVICE_CODE = #{_parameter}
and DEL_FLAG = '0'

select
from ar_device_realtime_para where DEL_FLAG = '0'
and DEVICE_CODE = #{deviceCode}
and PARA_NAME = #{paraName}
and CREATE_DATE > DATE_SUB(NOW(), INTERVAL #{minuteValue} MINUTE) order by CREATE_DATE DESC

mapper.update:
update ar_device_realtime_para
set
DEL_FLAG = '1'
where DEL_FLAG = '0'

    and DEVICE_CODE = #{deviceCode}


    and REMARKS = #{remarks}
